import { useState } from "react";

const DiaryEditor = () => {
  const [author, setAuthor] = useState("정준영");

  return (
    <div className="diaryEditor">
      <h2>오늘의 일기</h2>
      <div>
        <input
          value={author}
          onChange={(e) => {
            console.log(e.target.value);
            setAuthor(e.target.value);
          }}
        />
      </div>
    </div>
  );
};

export default DiaryEditor;
